import React, { useEffect, useState } from 'react';
import 'rbx/index.css';
import { Column, Container, Navbar, Button, Icon, Modal, Box } from 'rbx';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';

import ProductCard from './components/ProductCard';
import ShoppingCart  from './components/ShoppingCart';

const App = () => {
  const [data, setData] = useState({});
  const [shoppingCartFlag, setShoppingCartFlag] = useState(false);
  const [shoppingCartContent, setShoppingCartContent] = useState([]);

  const useForceUpdate = () => {
    const [value, set] = useState(true); //boolean state
    return () => set(value => !value); // toggle the state to force render
  }
  const forceUpdate = useForceUpdate();

  const products = Object.values(data);
  const id2product = {}
  let i;
  for (i = 0; i < products.length; i += 1) {
    id2product[products[i].sku] = products[i];
  }

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch('./data/products.json');
      const json = await response.json();
      setData(json);
    };
    fetchProducts();
  }, []);

  const addToCart = (productId, size) => {
    let i;
    let found = false;
    let content = shoppingCartContent;
    for (i = 0; i < content.length; i += 1) {
      if (content[i].productId === productId) {
        found = true;
        break;
      }
    }
    if (found) {
      content[i][size] += 1;
    }
    else {
      content.push({
        productId: productId,
        product: id2product[productId],
        's': 0,
        'm': 0,
        'l': 0,
        'xl': 0,
      });
      content[content.length - 1][size] += 1;
    }
    setShoppingCartContent(content);
    setShoppingCartFlag(true);
    forceUpdate();
  };

  useEffect(() => {
    console.log('shoppingCartContent changed', shoppingCartContent);
  });

  const removeFromCart = (productId, size) => {
    let i;
    let found = false;
    let content = shoppingCartContent;
    for (i = 0; i < content.length; i += 1) {
      if (content[i].productId === productId) {
        found = true;
        break;
      }
    }
    if (found) {
      content[i][size] = content[i][size] > 0 ? content[i][size] - 1 : 0;
    }
    else {
      content.push({
        productId: productId,
        product: id2product[productId],
        's': 0,
        'm': 0,
        'l': 0,
        'xl': 0,
      });
      content[i][size] = content[i][size] > 0 ? content[i][size] - 1 : 0;
    }
    setShoppingCartContent(content);
    forceUpdate();
  };

  return (
    <Container as='div' style={ {width: '100%', paddingTop: '20px'} }>
      <Navbar fixed='top' as='div' style={ {paddingLeft: '60px', paddingRight: '50px', paddingTop: '10px'} }>
      <Navbar.Brand>
        <Navbar.Item>
            <h1 style={{fontSize: '24px'}}><strong>Catalog</strong></h1>
          </Navbar.Item>
          <Navbar.Item>
          <Button color='black' onClick={ () => setShoppingCartFlag(true) }>
                <Icon>
                  <FontAwesomeIcon icon={ faShoppingCart }/>
                </Icon>
              </Button>
            </Navbar.Item>
        </Navbar.Brand>
      </Navbar>
      <Modal active={ shoppingCartFlag }>
        <Modal.Background />
        <Modal.Content>
        <Box>
            <Cart shoppingCartContentProp={ shoppingCartContent } removeFromCartFunc={ removeFromCart }/>
          </Box>
        </Modal.Content>
        <Modal.Close onClick={ () => setShoppingCartFlag(false) } />
      </Modal>
      <Column.Group multiline>
        <Column size='full'>
        </Column>
        {products.map(product =>
          <Column size='one-third'>
            <Product product={ product } addToCartFunc={ addToCart }/>
          </Column>
        )}
      </Column.Group>
    </Container>
);
};
export default App;
